            left: 0;
            right: 0;
            bottom: 0;
            background-image: 
                linear-gradient(rgba(100, 255, 218, 0.05) 1px, transparent 1px),
                linear-gradient(90deg, rgba(100, 255, 218, 0.05) 1px, transparent 1px);
            background-size: 30px 30px;
            z-index: 1;
        }

        /* 统一四边数据流效果 */
        .data-stream {
            position: absolute;
            background: linear-gradient(90deg, transparent, var(--cyber-accent), transparent);
            z-index: 2;
            opacity: 0.7;
        }

        .data-stream.top {
            top: 0;
            left: 0;
            width: 100%;
            height: 2px;
            animation: stream-horizontal 3s infinite linear;
        }

        .data-stream.right {
            top: 0;
            right: 0;
            width: 2px;
            height: 100%;
            background: linear-gradient(180deg, transparent, var(--cyber-accent), transparent);
            animation: stream-vertical 4s infinite linear;
            animation-delay: 0.5s;
        }

        .data-stream.bottom {
            bottom: 0;
            right: 0;
            width: 100%;
            height: 2px;
            animation: stream-horizontal-reverse 3s infinite linear;
            animation-delay: 1s;
        }

        .data-stream.left {
            bottom: 0;
            left: 0;
            width: 2px;
            height: 100%;
            background: linear-gradient(180deg, transparent, var(--cyber-accent), transparent);
            animation: stream-vertical-reverse 4s infinite linear;
            animation-delay: 1.5s;
        }

        @keyframes stream-horizontal {
            0% { transform: translateX(-100%); }
            100% { transform: translateX(100%); }
        }

        @keyframes stream-horizontal-reverse {
            0% { transform: translateX(100%); }
            100% { transform: translateX(-100%); }
        }

        @keyframes stream-vertical {
            0% { transform: translateY(-100%); }
            100% { transform: translateY(100%); }
        }

        @keyframes stream-vertical-reverse {
            0% { transform: translateY(100%); }
            100% { transform: translateY(-100%); }
        }

        /* 三角形角部标记 */
        .triangle-corner {
            position: absolute;
            width: 0;
            height: 0;
            z-index: 3;
            opacity: 0.9;
            filter: drop-shadow(0 0 4px var(--cyber-accent));
        }

        /* 左上角 - 指向右下方的三角形 */
        .triangle-corner.tl {
            top: 0;
            left: 0;
            border-top: 12px solid var(--cyber-accent);
            border-right: 12px solid transparent;
        }

        /* 右上角 - 指向左下方的三角形 */
        .triangle-corner.tr {
            top: 0;
            right: 0;
            border-top: 12px solid var(--cyber-accent);
            border-left: 12px solid transparent;
        }

        /* 左下角 - 指向右上方的三角形 */
        .triangle-corner.bl {
            bottom: 0;
            left: 0;
            border-bottom: 12px solid var(--cyber-accent);
            border-right: 12px solid transparent;
        }

        /* 右下角 - 指向左上方的三角形 */
        .triangle-corner.br {
            bottom: 0;
            right: 0;
            border-bottom: 12px solid var(--cyber-accent);
            border-left: 12px solid transparent;
        }

        /* 内部小三角形装饰 */
        .inner-triangle {
            position: absolute;
            width: 0;
            height: 0;
            z-index: 4;
            opacity: 0.6;
        }

        .inner-triangle.tl {
            top: 4px;
            left: 4px;
            border-top: 6px solid var(--cyber-accent);
            border-right: 6px solid transparent;
        }

        .inner-triangle.tr {
            top: 4px;
            right: 4px;
            border-top: 6px solid var(--cyber-accent);
            border-left: 6px solid transparent;
        }

        .inner-triangle.bl {
            bottom: 4px;
