        @keyframes stream-vertical {
            0% { transform: translateY(-100%); }
            100% { transform: translateY(100%); }
        }

        @keyframes stream-vertical-reverse {
            0% { transform: translateY(100%); }
            100% { transform: translateY(-100%); }
        }

        /* 三角形角部标记 */
        .triangle-corner {
            position: absolute;
            width: 0;
            height: 0;
            z-index: 3;
            opacity: 0.9;
            filter: drop-shadow(0 0 4px var(--cyber-accent));
        }

        /* 左上角 - 指向右下方的三角形 */
        .triangle-corner.tl {
            top: 0;
            left: 0;
            border-top: 12px solid var(--cyber-accent);
            border-right: 12px solid transparent;
        }

        /* 右上角 - 指向左下方的三角形 */
        .triangle-corner.tr {
            top: 0;
            right: 0;
            border-top: 12px solid var(--cyber-accent);
            border-left: 12px solid transparent;
