  @import url("https://fontsapi.zeoseven.com/121/main/result.css"); /* MuyaoPleased */

  :root{
    --outer-font: "MuyaoPleased","Microsoft YaHei",sans-serif;
    --container-width: 550px;
    --border-radius: 16px;

