    <!-- JavaScript 将在此处动态填充按钮 -->
    <div class="options-container" id="options-container" data-options-string="$1"></div>

    <script>
        // JavaScript部分无需改动，保持原样即可
        document.addEventListener('DOMContentLoaded', () => {
            const optionsContainer = document.getElementById('options-container');
            if (!optionsContainer) return;

            const optionTypes = [
                { type: 'Radical', className: 'is-radical', icon: 'fa-solid fa-bolt-lightning' },
                { type: 'Calm',    className: 'is-calm',    icon: 'fa-solid fa-brain' },
                { type: 'Magic',   className: 'is-magic',   icon: 'fa-solid fa-wand-magic-sparkles' },
                { type: 'Desire',  className: 'is-desire',  icon: 'fa-solid fa-heart-pulse' }
            ];

            const rawOptionsString = optionsContainer.dataset.optionsString;
            optionsContainer.removeAttribute('data-options-string');

            const options = rawOptionsString.split('|').map(s => s.trim());

            const handleSend = (text) => {
                try {
                    const inputArea = window.parent.document.querySelector('#send_textarea');
                    if (inputArea) {
                        if (inputArea.value.trim().length > 0) {
                            inputArea.value += '\n' + text;
                        } else {
                            inputArea.value = text;
                        }
                        inputArea.dispatchEvent(new Event('input', { bubbles: true }));
                    } else { throw new Error('SillyTavern UI not found.'); }
                } catch (error) {
                    console.error('Error sending message:', error);
                    alert(`自动操作失败，请手动复制: \n\n${text}`);
                }
            };

            options.forEach((text, index) => {
                if (!text || index >= optionTypes.length) return;

                const config = optionTypes[index];

                const button = document.createElement('button');
                button.className = 'option-btn ' + config.className;

                // 注意：原有的.inner-border div已被移除，因为效果已通过::before伪元素实现
                const contentWrapper = document.createElement('div');
                contentWrapper.className = 'option-content';

                const iconWrapper = document.createElement('span');
                iconWrapper.className = 'icon-wrapper';
                const icon = document.createElement('i');
                icon.className = config.icon;

                const textSpan = document.createElement('span');
                textSpan.className = 'option-text';
                textSpan.textContent = text;

                iconWrapper.appendChild(icon);
                contentWrapper.appendChild(iconWrapper);
                contentWrapper.appendChild(textSpan);

                button.appendChild(contentWrapper);

                button.onclick = () => handleSend(text);
                optionsContainer.appendChild(button);
            });
        });
    </script>
</body>
</html>
