{
"角色信息": {"角色名": "康娜·卡姆依 (Kanna Kamui)", "作品名": "《小林家的龙女仆》"},
"风格": "民族风哥特萝莉",
"上装": {"名称": "带毛领披肩", "细节": "白色绒毛质感的短款小披肩，领口用一个粉色的绒球装饰系带连接。披肩下摆为多层荷叶边，呼应裙子设计。"},
"连衣裙": {"名称": "民族风连衣裙", "细节": "主体为粉色和淡紫色的无袖连衣裙，上身部分有深紫色的民族风三角图案刺绣。裙摆分为三层，从上到下为淡紫色、粉色、深紫色，层层叠加并带有蕾丝花边，蓬松可爱。"},
"袜类": {"名称": "过膝长袜", "细节": "纯白色棉质过膝袜，袜口有蕾丝边装饰。"},
"鞋履": {"名称": "玛丽珍鞋", "细节": "暗粉色的圆头玛丽珍鞋，鞋面有一条简单的搭扣。"},
"配饰": {"名称": "龙角与龙尾", "细节": "头部两侧有一对白色的、向上弯曲的龙角。腰后有一个淡紫色、末端是绒球的龙尾，可通过插头状接口连接电源（设定）。"},
"发型与头饰": {"名称": "三角发饰与双马尾", "细节": "淡紫色长发，扎成两个低双马尾，每个马尾上用三颗深蓝色的椭圆形珠子固定。"}
},
{
"角色信息": {"角色名": "可莉 (Klee)", "作品名": "《原神》"},
"风格": "奇幻冒险家",
"上装": {"名称": "红色连衣裙", "细节": "鲜红色的泡泡袖连衣裙，领口为白色荷叶边，胸前有金色四叶草徽章和白色绒毛球装饰。"},
"下装": {"名称": "南瓜短裤", "细节": "白色灯笼短裤，作为打底，质感舒适便于活动。"},
"袜类": {"名称": "蕾丝短袜", "细节": "白色蕾丝花边短袜。"},
"鞋履": {"名称": "红色短靴", "细节": "深红色的圆头小皮靴，侧面有金色纽扣装饰。"},
"配饰": {"名称": "皮质手套与背包", "细节": "棕色露指皮手套。身后背着一个棕色硬皮小背包，上面挂着她最重要的伙伴——嘟嘟可（Dodoco）玩偶。"},
"发型与头饰": {"名称": "红色贝雷帽", "细节": "鲜红色的贝雷帽，侧面装饰有两根白色羽毛和一枚金色的四叶草徽章，与胸前徽章呼应。"}
},
{
"角色信息": {"角色名": "白 (Shiro)", "作品名": "《NO GAME NO LIFE 游戏人生》"},
"风格": "学院水手风",
"连衣裙": {"名称": "紫色水手连衣裙", "细节": "一件式的紫色长袖连衣裙，拥有巨大的白色水手领，领口系着一个明黄色的蝴蝶结。袖口为白色，带有紫色条纹。裙摆宽松，便于活动。"},
"袜类": {"名称": "过膝袜", "细节": "深蓝色（近黑色）的过膝长袜，紧贴腿部。"},
"鞋履": {"名称": "及踝靴", "细节": "与袜子同色的及踝短靴。"},
"配饰": {"名称": "迷你皇冠", "细节": "一个非常小巧的金色皇冠，歪戴在头顶一侧，象征着她在游戏世界的女王身份。"}
},
{
"角色信息": {"角色名": "血小板 (Platelet)", "作品名": "《工作细胞》"},
"风格": "可爱工装风",
"上装": {"名称": " oversizedT恤", "细节": "浅蓝色的大号短袖T恤，版型宽松，长度几乎能当连衣裙穿，体现了角色的娇小。"},
"下装": {"名称": "工装短裤", "细节": "米白色的工装短裤，被宽大的T恤几乎完全遮盖。"},
"鞋履": {"名称": "橡胶雨靴", "细节": "棕色的中筒橡胶雨靴，方便在“积水”环境中工作。"},
"发型与头饰": {"名称": "白色鸭舌帽", "细节": "一顶写着“血小板”三个汉字的白色鸭舌帽，帽檐略歪，显得俏皮可爱。"}
},
{
"角色信息": {"角色名": "四糸乃 (Yoshino)", "作品名": "《约会大作战》"},
"风格": "动物拟人雨衣",
"连衣裙": {"名称": "兔子连帽裙", "细节": "一件巨大的、看起来像雨衣的绿色连帽裙。帽子上有两只长长的兔耳，内侧是粉白波点图案。腰后有一条装饰性的白色绒球长尾巴。"},
"鞋履": {"名称": "雨靴", "细节": "与连衣裙配套的粉色雨靴，靴口有白色绒毛边。"},
"道具": {"名称": "手偶“四糸奈”", "细节": "左手上戴着一个腹黑兔子形象的手偶“四糸奈”，是角色的第二人格和最重要的朋友，手偶右眼戴着一个黑色眼罩。"},
"发型与头饰": {"名称": "自然卷发", "细节": "蓝色自然卷中长发，通常被巨大的帽子遮住大部分。"}
},
{
"角色信息": {"角色名": "和泉纱雾 (Izumi Sagiri)", "作品名": "《埃罗芒阿老师》"},
"风格": "家里蹲运动风",
"上装": {"名称": "绿色连帽运动外套", "细节": "标志性的绿色拉链连帽卫衣，通常拉链只拉到一半，露出里面的内搭，版型宽松，是典型的居家服。"},
"下装": {"名称": "运动短裤", "细节": "深灰色运动短裤，同样是居家风格。"},
"配饰": {"名称": "医用口罩", "细节": "在需要和人交流但又感到害羞时会戴上，遮住下半张脸，只露出一双大眼睛。"},
"发型与头饰": {"名称": "粉色蝴蝶结", "细节": "银白色长发，两侧各用一个粉色的蝴蝶结发饰扎起一小撮头发。"}
},
{
"角色信息": {"角色名": "贝아트리스 (Beatrice)", "作品名": "《Re:从零开始的异世界生活》"},
"风格": "华丽维多利亚萝莉",
"连衣裙": {"名称": "蝴蝶结礼服裙", "细节": "主色调为粉色与奶油色的多层礼服裙。上身有复杂的荷叶边和条纹装饰，胸前系着一个巨大的深红色蝴蝶结。袖子是蓬松的灯笼袖，裙摆层层叠叠，缀满了蕾丝和缎带。"},
"内搭": {"名称": "衬裙", "细节": "为了让裙子有足够的蓬度，内里需要穿着多层硬纱衬裙。"},
"袜类": {"名称": "条纹裤袜", "细节": "粉色与白色相间的竖条纹裤袜。"},
"鞋履": {"名称": "搭扣皮鞋", "细节": "深红色的圆头皮鞋，鞋跟不高，鞋面有蝴蝶结装饰。"},
"发型与头饰": {"名称": "钻头发型与巨大蝴蝶结", "细节": "奶油色的长发被卷成两个夸张的巨大螺旋状双马尾（俗称钻头），发根处各系着一个深红色的蝴蝶结，与胸前呼应。"}
},
{
"角色信息": {"角色名": "七七 (Qiqi)", "作品名": "《原神》"},
"风格": "中式僵尸（奇幻）",
"上装": {"名称": "对襟短褂", "细节": "深紫色的中式对襟短褂，袖口宽大，边缘有祥云图案刺绣。"},
"下装": {"名称": "灯笼短裤", "细节": "淡紫色的灯笼短裤。"},
"袜类": {"名称": "不对称长袜", "细节": "右腿为缠绕至大腿的白色绷带，左腿为纯白色的过膝袜。"},
"鞋履": {"名称": "布鞋", "细节": "深色的中式布鞋，鞋底厚实。"},
"配饰": {"名称": "符咒与配饰", "细节": "腰间挂着一个巨大的冰蓝色“神之眼”，背后系着一个带有冰花图案和紫色流苏的装饰。"},
"发型与头饰": {"名称": "中式官帽与符咒", "细节": "深蓝色的六角形中式官帽，侧面有红色流苏。额前贴着一张写有“敕令”字样的符咒，垂下的部分遮住了右眼，是僵尸身份的象征。"}
},
{
"角色信息": {"角色名": "萝莉·麦丘利 (Rory Mercury)", "作品名": "《GATE 奇幻自卫队》"},
"风格": "哥特战斗神官",
"连衣裙": {"名称": "哥特神官服", "细节": "以黑色和红色为主色调的哥特萝莉风格连衣裙。裙子主体为黑色，拥有大量的红色蕾丝、荷叶边和蝴蝶结装饰。泡泡袖设计，腰部收紧，裙摆巨大且华丽。"},
"袜类": {"名称": "吊带袜", "细节": "黑色的过膝吊带袜，袜口有红色蕾丝。"},
"鞋履": {"名称": "红色长靴", "细节": "及膝的红色绑带长靴，带有一定的鞋跟。"},
"发型与头饰": {"名称": "黑色蕾丝发箍", "细节": "黑色长直发，头上戴着一个宽大的黑色蕾丝发箍，中间有红色缎带装饰。"},
"道具": {"名称": "巨大战斧", "细节": "与娇小身材形成巨大反差的金属战斧，斧柄极长，斧刃巨大，是她的主要武器和身份象征。"}
},
{
"角色信息": {"角色名": "逢坂大河 (Aisaka Taiga)", "作品名": "《龙与虎》"},
"风格": "日系不良JK",
"上装": {"名称": "红色西装外套", "细节": "非常具有标志性的鲜红色单排扣西装外套，版型略微宽松，袖子有时会卷起来。"},
"内搭": {"名称": "白色衬衫与领结", "细节": "标准的白色长袖衬衫，领口系着一个蓝色的领结。"},
"下装": {"名称": "百褶裙", "细节": "深蓝色的百褶短裙，裙长在膝盖以上。"},
"袜类": {"名称": "黑色长袜", "细节": "长度到小腿肚的黑色学生袜。"},
"鞋履": {"名称": "乐福鞋", "细节": "棕色的日式学生乐福鞋。"}

